mapper.update:
UPDATE t_eva_queryselect
qry_id=#{qryId}, tb_name=#{tbName}, alias=#{alias}, col=#{col}, remark=#{remark}, creater_id=#{createrId}, creater_name=#{createrName}, updater_id=#{updaterId}, updater_name=#{updaterName}, create_date=#{createDate}, update_date=#{updateDate},
WHERE id =#{id}

mapper.delete:
        DELETE FROM  t_eva_queryselect 


                AND id = #{map.id}


				AND qry_id = #{map.qryId}


				AND tb_name = #{map.tbName}


				AND alias = #{map.alias}


				AND col = #{map.col}


				AND remark = #{map.remark}


				AND creater_id = #{map.createrId}


				AND creater_name = #{map.createrName}


				AND updater_id = #{map.updaterId}


				AND updater_name = #{map.updaterName}


				AND create_date = #{map.createDate}


				AND update_date = #{map.updateDate}

mapper.select:
        SELECT

        FROM t_eva_queryselect


			order by ${condition.sort}


            limit ${offset}, ${rows}

