mapper.update:
UPDATE t_eva_querywhere
qry_id=#{qryId}, tb_name=#{tbName}, col=#{col}, formula=#{formula}, val=#{val}, col_type=#{colType}, creater_id=#{createrId}, creater_name=#{createrName}, updater_id=#{updaterId}, updater_name=#{updaterName}, create_date=#{createDate}, update_date=#{updateDate},
WHERE id =#{id}

mapper.delete:
        DELETE FROM  t_eva_querywhere 


                AND id = #{map.id}


				AND qry_id = #{map.qryId}


				AND tb_name = #{map.tbName}


				AND col = #{map.col}


				AND formula = #{map.formula}


				AND val = #{map.val}


				AND col_type = #{map.colType}


				AND creater_id = #{map.createrId}


				AND creater_name = #{map.createrName}


				AND updater_id = #{map.updaterId}


				AND updater_name = #{map.updaterName}


				AND create_date = #{map.createDate}


				AND update_date = #{map.updateDate}

