mapper.update:
UPDATE t_oa_flow
name=#{name}, table_name=#{tableName}, start_status=#{startStatus}, end_status=#{endStatus}, modify_status=#{modifyStatus}, modify_flag=#{modifyFlag}, create_date=#{createDate}, update_date=#{updateDate}, name_space=#{nameSpace},
WHERE id =#{id}

mapper.delete:
        DELETE FROM  t_oa_flow 


                AND id = #{map.id}


				AND name = #{map.name}


				AND table_name = #{map.tableName}


				AND start_status = #{map.startStatus}


				AND end_status = #{map.endStatus}


				AND modify_status = #{map.modifyStatus}


				AND modify_flag = #{map.modifyFlag}


				AND create_date = #{map.createDate}


				AND update_date = #{map.updateDate}


				AND name_space = #{map.nameSpace}

mapper.select:
        SELECT

        FROM t_oa_flow


			order by ${condition.sort}


            limit ${offset}, ${rows}

        SELECT

        FROM t_oa_flow


                AND id = #{map.id}


				AND name = #{map.name}


				AND table_name = #{map.tableName}


				AND start_status = #{map.startStatus}


				AND end_status = #{map.endStatus}


				AND modify_status = #{map.modifyStatus}


				AND modify_flag = #{map.modifyFlag}


				AND create_date = #{map.createDate}


				AND update_date = #{map.updateDate}


				AND name_space = #{map.nameSpace}


        ORDER BY ${orderBy} ${sortBy}

        SELECT

        FROM t_oa_flow


                AND id = #{map.id}


				AND name = #{map.name}


				AND table_name = #{map.tableName}


				AND start_status = #{map.startStatus}


				AND end_status = #{map.endStatus}


				AND modify_status = #{map.modifyStatus}


				AND modify_flag = #{map.modifyFlag}


				AND create_date = #{map.createDate}


				AND update_date = #{map.updateDate}


				AND name_space = #{map.nameSpace}


        limit 0,1
