mapper.update:
UPDATE t_oa_flowstep
flow_id=#{flowId}, flow_name=#{flowName}, name=#{name}, before_status=#{beforeStatus}, after_status=#{afterStatus}, step_order=#{stepOrder}, create_date=#{createDate}, update_date=#{updateDate},
WHERE id =#{id}

mapper.select:
        SELECT

        FROM t_oa_flowstep


			order by ${condition.sort}


            limit ${offset}, ${rows}

        SELECT

        FROM t_oa_flowstep


                AND id =#{map.id}


				AND flow_id = #{map.flowId}


				AND flow_name = #{map.flowName}


				AND name = #{map.name}


				AND status = #{map.status}


				AND step_order = #{map.stepOrder}


				AND create_date = #{map.createDate}


				AND update_date = #{map.updateDate}


        ORDER BY ${orderBy} ${sortBy}

        SELECT

        FROM t_oa_flowstep


                AND id =#{id}


				AND flow_id = #{flowId}


				AND flow_name = #{flowName}


				AND name = #{name}


				AND before_status = #{beforeStatus}


				AND after_status = #{afterStatus}


				AND step_order = #{stepOrder}


				AND create_date = #{createDate}


				AND update_date = #{updateDate}


        limit 0,1
